    def displayTime(self, Form):
        dur = str(round(SquatExercise.getDuration(), 1))
        Form.squat_label.setText("Hold: " + dur)

    def squats(self, Form, deg):
        global squatState, celebrate
        squatState = SquatExercise.squatRep(int(deg), int(maxLegBend), int(minLegBend), tolerance, squatState)
        if squatState == 0:
            Form.squat_image.setStyleSheet("background-image: url(:/images/1st.jpeg);")
            Form.squat_label.setText("Squat Down!")
        elif squatState == 1:
            self.displayTime(Form)
        elif squatState == 2:
            Form.squat_image.setStyleSheet("background-image: url(:/images/2nd.jpeg);")
            Form.squat_label.setText("Go Up!")
        elif squatState == 3:
            self.displayTime(Form)

        # print("SquatState: " + str(squatState))
        curReps = SquatExercise.getReps()
        Form.curr_val.setText("Current Value: " + deg)
        Form.numReps.setText(str(curReps))
        if curReps == squatReps:
            SquatExercise.zeroReps()
            celebrate = True

    def setValues(self, Form, max, min, tol, sReps, dur):
        global maxLegBend, minLegBend, tolerance, squatReps
