        tolerance = tol
        squatReps = sReps
        SquatExercise.setDuration(dur)

        Form.hi_val.setText("Straight Value: " + minLegBend)
        Form.lo_val.setText("Squat Value: " + maxLegBend)
        Form.rep_label.setText("Goal: " + str(squatReps))

