        # TODO: Add Serial Input code
        def startReading(self):
                global programOn
                self.calib_btn.setText("Start Program" if programOn else "Stop Program")
                print(self.serial.isOpen())
                if not self.serial.isOpen():
                        if not self.serial.open(QtCore.QIODevice.ReadWrite):
                                print("Apparently the serial opens and never runs through this")
                        programOn = True
                        print("Serial Started")
                else:
                        self.serial.close()
                        programOn = False

        def receive(self):
                global capPin, deg, tuckCal, restCal, extendedCal, capSum, lastTime

                while self.serial.canReadLine():
                        text = self.serial.readLine().data().decode()
                        text = text.rstrip('\r\n')
                        tempText = text.rsplit(" ")

                        capPin = tempText[0]
                        deg = tempText[1].split(".")[0]

                        c = int(capPin)
                        f = int(deg)
                        # print("Cap: " + capPin + " Flex: " + deg)
                        capSum = capSum + c
                        if time.perf_counter() - lastTime > 0.25:
                                output = letterCode.getLetter(capSum, f, int(tuckCal), int(restCal), int(extendedCal))
                                self.textEdit.append(output + " > out")
                                self.keyboard.press(output)
                                self.keyboard.release(output)
                                # self.changeUILetters()
                                capSum = 1
                                lastTime = time.perf_counter()



