        def action_items(self):
                self.calib_btn.clicked.connect(self.calibration)

        # things to do when confirm button is clicked
        # TODO: link calibration code
        def calibration(self):
                global tuckCal, restCal, extendedCal, calFormShowed, calibrated
                if not calibrated:
                        if Form.isHidden():
                                if not calFormShowed:
                                        Form.show()
                                        self.textEdit.setText("Let's calibrate!")
                                        calFormShowed = True
                                else:
                                        self.checkCalibration()
                        else:
                                Form.hide()
                                self.checkCalibration()
                                print(calibration.getCalVals())
                else:
                        self.startReading()

        def checkCalibration(self):
                global tuckCal, restCal, extendedCal, calibrated, calTemp
                tuckCal, restCal, extendedCal = calibration.getCalVals()
                print(calibration.calGot())
                if calibration.calGot():
                        calibrated = True
                        self.textEdit.append(str(tuckCal) + " > tuckCal")
                        self.textEdit.append(str(restCal) + " > restCal")
                        self.textEdit.append(str(extendedCal) + " > extendedCal")
                        self.calib_btn.setText("Start Program")


